INSERT INTO daily_tips (id, title, description, category, points_reward, created_at) VALUES 
(1, 'Mang bình nước cá nhân', 'Sử dụng bình nước tái sử dụng thay vì chai nhựa dùng một lần giúp giảm lượng rác thải nhựa khổng lồ.', 'Sống xanh', 10, NOW()),
(2, 'Tắt thiết bị điện', 'Rút phích cắm hoặc tắt công tắc các thiết bị điện khi không sử dụng để tiết kiệm năng lượng.', 'Tiết kiệm điện', 10, NOW()),
(3, 'Phân loại rác tại nguồn', 'Tách rác hữu cơ và vô cơ giúp quy trình tái chế và xử lý rác thải hiệu quả hơn.', 'Phân loại rác', 15, NOW());